    private void jbCrearEmpresaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCrearEmpresaActionPerformed
        if (jtfRazonSocial.getText().isEmpty() || jtfCuit.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Campos vacíos.");
            return;
        }

        try {
            String razonSocial = jtfRazonSocial.getText();
            int cuit = Integer.parseInt(jtfCuit.getText());

            Empresa empresa = new Empresa(razonSocial, cuit);

            if (corroborarEmpresa(empresa)) {
                JOptionPane.showMessageDialog(this, "Empresa creada exitosamente.");

                jcbEmpresas.addItem(empresa);
                empresas.add(empresa);
            } else {
                JOptionPane.showMessageDialog(this, "No se puede crear una empresa ya existente.");
            }

            jtfRazonSocial.setText("");
            jtfCuit.setText("");
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Error en el campo de CUIT. (solo números)");
        }
    }//GEN-LAST:event_jbCrearEmpresaActionPerformed

    private void jbGuardarEmpleadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbGuardarEmpleadoActionPerformed
        
        if (jtfDocumento.getText().isEmpty() || jtfNombre.getText().isEmpty() || jtfApellido.getText().isEmpty() || jtfSueldo.getText().isEmpty() || jcbEmpresas.getSelectedIndex() == -1) {
            JOptionPane.showMessageDialog(this, "Campos vacíos.");
            return; 
        }
        
        try {
            
            int dni = Integer.parseInt(jtfDocumento.getText());
            String nombreApellido = jtfNombre.getText() + " " + jtfApellido.getText();
            String categoria = (String) jcbCategoria.getSelectedItem();
            double sueldo = Double.parseDouble(jtfSueldo.getText());
            Empresa empresa = (Empresa) jcbEmpresas.getSelectedItem();
            
            Empleado empleado = new Empleado(dni, nombreApellido, categoria, sueldo, empresa);
            
            if (corroborarEmpleado(empresa, empleado)) {
                
                empresa.agregarEmpleado(empleado);
                JOptionPane.showMessageDialog(this, "Empleado creado exitosamente.");
            } else {
                JOptionPane.showMessageDialog(this, "El empleado ya existe dentro de la empresa " + empresa.getRazonSocial());
            }
            
            jtfDocumento.setText("");
            jtfNombre.setText("");
            jtfApellido.setText("");
            jtfSueldo.setText("");
            jcbEmpresas.setSelectedIndex(-1);
            
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Error en el campo de texto (solo numeros).");
        }
        
    }//GEN-LAST:event_jbGuardarEmpleadoActionPerformed

    private void jbMostrarEmpleadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbMostrarEmpleadosActionPerformed
        Empresa empresa = (Empresa) jcbEmpresas.getSelectedItem();
        
        JOptionPane.showMessageDialog(this, "Empleados de " + empresa.getRazonSocial().toUpperCase() +
                ":\n" + empresa.mostrarEmpleados());
    }//GEN-LAST:event_jbMostrarEmpleadosActionPerformed

    private void jcbEmpresasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jcbEmpresasActionPerformed
        if (jcbEmpresas.getSelectedIndex() != -1) {
            jbGuardarEmpleado.setEnabled(true);
            jbMostrarEmpleados.setEnabled(true);
        } else {
            jbGuardarEmpleado.setEnabled(false);
            jbMostrarEmpleados.setEnabled(false);
        }
    }//GEN-LAST:event_jcbEmpresasActionPerformed

    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem1ActionPerformed
        escritorio.removeAll();
        escritorio.repaint();
        EmpleadosView ventana = new EmpleadosView();
        ventana.setVisible(true);
        escritorio.add(ventana);
        escritorio.moveToFront(ventana);
    }//GEN-LAST:event_jMenuItem1ActionPerformed

