    private boolean corroborarEmpresa(Empresa emp) {
        for (Empresa empresa : empresas) {
            if (empresa.equals(emp)) {
                return false;
            }
        }
        return true;
    }

    private boolean corroborarEmpleado(Empresa empresa, Empleado emp) {
        for (Empleado empleado : empresa.getEmpleados()) {
            
            if (empleado.equals(emp)) {
                return false;
            }
            
        }
        return true;
    }
